import React, { useEffect, useState } from "react";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from "recharts";
import axios from "axios";

const SensorCharts = () => {
  const [sensorData, setSensorData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:4000/api/sensor-data");
        setSensorData(response.data);
      } catch (error) {
        console.error("Error fetching sensor data:", error);
      }
    };

    fetchData();
    const interval = setInterval(fetchData, 5000); // Refresh data every 5 seconds

    return () => clearInterval(interval);
  }, []);

  return (
    <div>
      <h2>Individual Sensor Readings</h2>
      <ResponsiveContainer width="100%" height={300}>
        <LineChart data={sensorData}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="timestamp" tickFormatter={(time) => new Date(time).toLocaleTimeString()} />
          <YAxis />
          <Tooltip />
          <Legend />
          <Line type="monotone" dataKey="RGB.red" stroke="red" name="Red" />
          <Line type="monotone" dataKey="RGB.green" stroke="green" name="Green" />
          <Line type="monotone" dataKey="RGB.blue" stroke="blue" name="Blue" />
          <Line type="monotone" dataKey="lightIntensity" stroke="orange" name="Light Intensity" />
        </LineChart>
      </ResponsiveContainer>

      <h2>Combined Sensor Data</h2>
      <ResponsiveContainer width="100%" height={300}>
        <LineChart data={sensorData}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="timestamp" tickFormatter={(time) => new Date(time).toLocaleTimeString()} />
          <YAxis />
          <Tooltip />
          <Legend />
          <Line type="monotone" dataKey="RGB.red" stroke="red" name="Red" />
          <Line type="monotone" dataKey="RGB.green" stroke="green" name="Green" />
          <Line type="monotone" dataKey="RGB.blue" stroke="blue" name="Blue" />
          <Line type="monotone" dataKey="lightIntensity" stroke="orange" name="Light Intensity" />
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
};

export default SensorCharts;
