import React, { useEffect, useState } from "react";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from "recharts";

const SensorGraphs = () => {
  const [sensorData, setSensorData] = useState([]);

  useEffect(() => {
    fetch("http://localhost:3000/api/sensor-data")
      .then((res) => res.json())
      .then((data) => {
        setSensorData(data.map(entry => ({
          ...entry,
          timestamp: new Date(entry.timestamp).toLocaleTimeString(), // Format time
        })));
      })
      .catch((err) => console.error("Error fetching data:", err));
  }, []);

  return (
    <div>
      <h2>Color Sensor Data</h2>
      <ResponsiveContainer width="100%" height={300}>
        <LineChart data={sensorData}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="timestamp" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Line type="monotone" dataKey="RGB.red" stroke="red" />
          <Line type="monotone" dataKey="RGB.green" stroke="green" />
          <Line type="monotone" dataKey="RGB.blue" stroke="blue" />
        </LineChart>
      </ResponsiveContainer>

      <h2>Photodiode Sensor (Light Intensity)</h2>
      <ResponsiveContainer width="100%" height={300}>
        <LineChart data={sensorData}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="timestamp" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Line type="monotone" dataKey="lightIntensity" stroke="orange" />
        </LineChart>
      </ResponsiveContainer>

      <h2>Combined Sensor Data</h2>
      <ResponsiveContainer width="100%" height={300}>
        <LineChart data={sensorData}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="timestamp" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Line type="monotone" dataKey="RGB.red" stroke="red" />
          <Line type="monotone" dataKey="RGB.green" stroke="green" />
          <Line type="monotone" dataKey="RGB.blue" stroke="blue" />
          <Line type="monotone" dataKey="lightIntensity" stroke="orange" />
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
};

export default SensorGraphs;
